local function editor_drawer(v)
	v.fadeScreen(0xFF00, 15)

	local shaded_graphic = TBS_Polygon.shaderGraphic(background_img, function(x, y, colorx, width, height)
		local time_delta = abs(((leveltime/8) % 6)-3)
