	end)




	TBS_Polygon.drawPolygonFill(v, 0, 0, default_polygon, shaded_graphic, leveltime/4 % 128, leveltime/8 % 128, 0, 0)
	TBS_Polygon.drawPolygon(v, 0, 0, default_polygon, 37, 1, true)
	for i = 1,#default_polygon do
		v.drawString(250, 50+i*10, i..": x "..default_polygon[i].x.." y "..default_polygon[i].y)
		if TBS_Menu.draw_DragableFill(v, default_polygon[i].x-1, default_polygon[i].y-1, 3, 3, 134) then
			v.drawFill(default_polygon[i].x-1, default_polygon[i].y-1, 3, 3, 64)
			default_polygon[i].x = TBS_Menu.mouse_x
			default_polygon[i].y = TBS_Menu.mouse_y
		end
	end
	
	TBSlib.fontdrawerInt(v, 'MA14LT', 0, 20, "TEST", 0, v.getColormap(TC_DEFAULT, PKZ_Table.getHueFontColor(8)), "left", 0, 1, "0")	
	
	TBSlib.fontdrawerInt(v, 'MA14LT', 0, 40, "TEST", 0, v.getColormap(TC_DEFAULT, PKZ_Table.current_rainbowfontcolor), "left", 0, 1, "0")	
end

local function viewer_drawer(v)
	v.fadeScreen(0xFF00, 15)

	v.drawFill(0, 0, 320, 200, 255)
	local poly_draw = TBS_Polygon.progressLine(wonder_windup_poly, ((1+(leveltime % 24)) << FRACBITS)/24)
	
	if #poly_draw > 2 then
		TBS_Polygon.drawPolygonFill(v, 0, 0, poly_draw, img_draw, 128+12, 128+12, 0, 0)
	end
end


